let navMenu=document.querySelectorAll(".nav_depth1 .list_depth1");function navFunction(){navMenu.forEach(e=>{e.addEventListener("mouseenter",function(){this.classList.add("active")}),e.addEventListener("mouseleave",function(){this.classList.remove("active")})})}let searchBtn=document.querySelector(".btn_open_search"),searchClose=document.querySelector(".search_close"),searchArea=document.querySelector(".search_area"),headerDim=document.querySelector("header"),searchFlg=!1,searchFunc=e=>{e?(searchFlg=e,searchArea.classList.add("active"),headerDim.classList.add("dimed")):(searchFlg=e,searchArea.classList.remove("active"),headerDim.classList.remove("dimed"))};function searchFunction(){searchBtn.addEventListener("click",()=>searchFunc(!0)),searchClose.addEventListener("click",()=>searchFunc(!1))}export{navFunction,searchFunction};